mapper.update:
UPDATE students
SET full_name = #{fullName}, furigana = #{furigana}, handle_name = #{handleName}, mail_address =
#{mailAddress}, area = #{area}, age = #{age}, gender = #{gender}, remark = #{remark}, is_deleted
= #{isDeleted}
WHERE id = #{id}

UPDATE students_courses
SET course_name = #{courseName}
WHERE course_id = #{courseId}

UPDATE students_courses_status
SET course_status = #{courseStatus}
WHERE course_id = #{courseId}
